mapper.select:
SELECT DISTINCT testNo , teacherNo , testTitle , testDate , teacherName , studentNo , score FROM (SELECT test.test_no testNo , test.teacher_no teacherNo , test.test_title testTitle , test.test_date testDate , teacher.teacher_name teacherName , student.student_no studentNo , score.score score FROM test INNER JOIN teacher ON test.teacher_no = teacher.teacher_no INNER JOIN score ON test.test_no = score.test_no INNER JOIN student ON score.student_no = student.student_no UNION SELECT test.test_no testNo , test.teacher_no teacherNo , test.test_title testTitle , test.test_date testDate , teacher.teacher_name teacherName , null studentNo , null score FROM test INNER JOIN teacher ON test.teacher_no = teacher.teacher_no) t WHERE (studentNo = #{studentNo} OR studentNo IS NULL)
AND t.teacherNo = #{teacherNo}
AND test_title LIKE CONCAT('%', #{searchWord}, '%')
GROUP BY testNo ORDER BY t.teacherNo DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT count(*) FROM test
test.teacher_no = #{teacherNo}
AND test_title LIKE CONCAT('%', #{searchWord}, '%')
